<template>
  <div id="app" @mouseup="resetJoy">
    <div class="app-wrapper">
      <h1 class="q-pa-sm text-center hero-heading">
        Reaction Game
      </h1>

      <div
        id="PS-body"
        class="row q-mx-auto"
        :style="{
          transform:
            'rotateX(' + -joyPosY / 5 + 'deg) rotateY(' + joyPosX / 8 + 'deg) ',
        }"
      >
        <div class="ps-wrapper row justify-center">
          <div id="L-set">
            <div id="L-joystick">
              <div
                id="L-joystick-pad"
                @mousedown="startMove"
                @mousemove="moveAround"
                :style="{
                  transform: 'translate(' + joyPosX + 'px, ' + joyPosY + 'px)',
                }"
              ></div>
            </div>

            <div id="color-buttons">
              <q-btn
                push
                round
                id="btn-top"
                padding="0"
                icon="change_history"
                style="color: #474747; backgroundColor: #232323"
                :class="open && 'text-yellow'"
                @click="() => this.changeColor('yellow')"
              />
              <q-btn
                push
                round
                id="btn-left"
                padding="0"
                icon="crop_square"
                style="color: #474747; backgroundColor: #232323"
                :class="open && 'text-green'"
                @click="() => this.changeColor('green')"
              />
              <q-btn
                push
                round
                id="btn-right"
                padding="0"
                icon="radio_button_unchecked"
                style="color: #474747; backgroundColor: #232323"
                :class="open && 'text-red'"
                @click="() => this.changeColor('red')"
              />
              <q-btn
                push
                round
                id="btn-bottom"
                padding="0"
                icon="close"
                style="color: #474747; backgroundColor: #232323"
                :class="open && 'text-blue'"
                @click="() => this.changeColor('blue')"
              />
            </div>
          </div>

          <div
            class="back-screen"
            :class="[open === null ? '' : open ? 'pspStart' : 'pspClose']"
